sys_ui_script.script:
class Button extends ReactorComponent
{
	constructor(props)
	{
		super(props);
	}

	onClick()
	{
		if (typeof this.props.callbackOnClick == 'function')
		{
			this.props.callbackOnClick(this.props.value);
		}
	}

	render()
	{
		var strText = GetStringValue(this.props.text);
		if (strText != '')
		{
			var inner = strText;
		}
		else
		{
			var inner = this.props.children;
		}
		return e('button',
			{
				onClick: this.onClick
			},
			inner);	
	}	
}
