sys_ui_script.script:
class Centered extends ReactorComponent
{
	constructor(props)
	{
		super(props);
	}

	render()
	{
		return e('div',
			{
				style:
				{
					height: '100%',
					width: '100%',
					display: 'flex',
					alignItems: 'center',
					justifyContent: 'center'					
				}
			},
			e('div',
				{
					style:
					{
						//overflowY: 'auto',
						maxHeight: '99%' // this avoids the effect that the centered element is cut-off if the screen height is too small. CSS mystery.
					}
				},
				this.props.children));	
	}	
}
