sys_ui_script.script:
class Workspace extends ReactorComponent
{
	constructor(props)
	{
		super(props);
	}

	render()
	{

		var aComponents = [];

		var logo = e('div',
			{
				style: {
					backgroundColor: '#eeeeee',
					float: 'left',
					minWidth: '20'
				}
			},this.props.logo);		


		var menu = e('div',
			{
				style: {
					backgroundColor: '#dddddd',
				}
			},this.props.menu);		

		var user = e('div',
			{
				style: {
					backgroundColor: '#eeeeee',
					float: 'right',
					minWidth: '20'
				}
			},this.props.user);		

		var headerbar = e('div',
			{
				style: {
					width: '100%'
				}
			},[
				logo,
				user,
				menu
			]);


		var toolbar = e('div', { backgroundColor: '#bbbbbb' }, this.props.toolbar);


		var status = e('div',{ backgroundColor: '#aaaaa'},this.props.status);

		var footerbar = e('div',
			{
				style: {
					backgroundColor: '#eeeeee',
					width: '100%',
					position: 'absolute',
					bottom: '0'
				}
			},[
				status
			]);


		aComponents.push(headerbar);
		aComponents.push(toolbar);
		aComponents.push(footerbar);

		return e(Fullscreen,{},aComponents);	
	}	
}
