x_snc_reactor_component.script:
class Error extends ReactorComponent
{
	constructor(props)
	{
		super(props);
	}

	render()
	{
		var strText = GetStringValue(this.props.text);
		if (strText == '')
		{
			return e('div',{});
		}
		return e('div',
			{
				className: 'outputmsg outputmsg_error notification notification-danger'
			},
			e('div',
				{
					className: 'outputmsg_text'
				},
				this.props.text));	
	}	
}
