x_snc_reactor_component.script:
class Clock extends ReactorComponent
{
	constructor(props)
	{
		super(props);
		this.state = {date: new Date()};
		this.onTick = this.onTick.bind(this);
	}

	componentDidMount()
	{
		this.timerID = setInterval(this.onTick,1000);
	}

	componentWillUnmount()
	{
		clearInterval(this.timerID);
	}

	onTick()
	{
		this.setState({
			date: new Date()
		});
	}

	render()
	{
		var strTimeString = this.state.date.toString();
		//var strTimeString = this.state.date.toLocaleTimeString();
		return e('div',null,
			e('span',null,strTimeString)
			);
	}
}
