x_snc_reactor_component.script:
class Workspace extends ReactorComponent
{
	constructor(props)
	{
		super(props);
	}

	render()
	{

		var aComponents = [];

		var headermenu = e('div',{ backgroundColor: '#eeeeee'},this.props.menu);

		var statusbar = e('div',{ backgroundColor: '#aaaaa'},this.props.status);

		aComponents.push(headermenu);
		aComponents.push(statusbar);

		return e(Fullscreen,{},aComponents);	
	}	
}
